import { Component, OnInit } from '@angular/core';
import { RepuestoService } from '../../core/repuesto.service';
import { Repuesto } from '../../shared/repuesto';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-repuesto-detail',
  templateUrl: './repuesto-detail.component.html',
  styleUrls: ['./repuesto-detail.component.css'],
})
export class RepuestoDetailComponent implements OnInit {
  repuesto: Repuesto = {
    id: 0,
    name: '',
    price: 0,
    shortDescription: '',
    description: '',
    category: '',
    image: '',
  };
  prodId: number = 0;

  constructor(
    private activatedroute: ActivatedRoute,
    private router: Router,
    private repuestoService: RepuestoService
  ) {}

  ngOnInit() {
    this.prodId = parseInt(this.activatedroute.snapshot.params['repuestoId']);
    this.repuestoService
      .getRepuestoById(this.prodId)
      .subscribe((data: Repuesto) => (this.repuesto = data));
  }
  goEdit(): void {
    this.router.navigate(['/repuestos', this.prodId, 'edit']);
  }
  onBack(): void {
    this.router.navigate(['']);
  }
}
<figure class="figure">
  <img src="{{repuesto.image}}">
  <figcaption class="figure-caption">
    <h4>Nombre: {{repuesto.name}}</h4>
    <h5>Precio: {{repuesto.price}}€</h5>
    <h5> Descripcion: {{repuesto.description}}</h5>
    <h5>Categoria: {{repuesto.category}}</h5>
  </figcaption>
</figure>

<div class="row mt-4">
  <div class="col-md-4">
    <button class="btn btn-secondary mr-3"
            style="width:80px"
            (click)="onBack()">
      <i class="fa fa-chevron-left"></i> Atrás
    </button>
    <button class="btn btn-primary"
            style="width:80px"
            (click)="goEdit()">
      Editar
    </button>
  </div>
</div>
